def calculaAck(m, n):
    if m == 0:
        return n+1
    if n == 0:
        return calculaAck(m-1, 1)
    return calculaAck(m-1, calculAck(m, n-1))

				calculaAck


				m=2;n=2


				m=3;n=4


				m=4;n=3


