def binomial(n, k):
    if k == 0:
        return 1
    return n * binomial(n - 1, k - 1) // k

				binomial

def binomial_iter(n, k):
    lmul = rdiv = 1
    while k > 0:
        (n, k, lmul, rdiv) = (n - 1, k - 1, lmul * n, k * rdiv)
    return lmul // rdiv

				binomial_iter

def step(n, k, previous_x): 
    x = n * previous_x // k
    return (n + 1, k + 1, x)

def binomialStep(n, k):
    t = k
    (n, k, previous_x) = (n - k + 1, 1, 1)
    for _i in xrange(1, t + 1):
        (n, k, previous_x) = step(n, k, previous_x)
    return previous_x

				binomialStep

