def g2(n):
    if n <= 3:
        return n
    a, b, c = 1, 2, 3
    for i in range(n - 3):
        a, b, c = b, c, c + 2 * b + 3 * a
    return c

				g2

def g3(n):
    if n <= 3:
        return n
    a, b, c = 1, 2, 3
    while n > 3:
        a, b, c = b, c, c + 2 * b + 3 * a
        n -= 1
    return c

				g3

def g4(n):
    if n <= 3:
        return n
    a, b, c = 1, 2, 3
    while n > 3:
        a, b, c = b, c-a, c + 2 * b + 3 * a
        n -= 1
    return c

				g4

def g5(n):
    if n == 3:
        return n
    a, b, c = 1, 2, 3
    while n > 3:
        b, a, c = c-a,b, c + 2 * b + 3 * a
        n -= 1
    return c

				g5

def g6(n):
    if n == 3:
        return n
    a, b, c = 2, 1, 3
    while n > 3:
        b, a, c = c-a,b, c + 2 * b + 2 * a
        n =n- 1
    return c

				g6
